  },
  password: {
    type: String,
    // required: true,
  },
  phonenumber: {
    type: String,
    required: true,
    unique: true
  },
  isActive: {
    type: Boolean,
    default: true
  },
});

const User = mongoose.model("User", UserSchema);

export default User;

