sourcesContent:
import userHelper from "../helpers/userHelpers";
import twilioFunctions from "../config/twilio";
import dotenv from "dotenv";
import userHelpers from "../helpers/userHelpers";

import User from "../models/userModels";
import Address from "../models/addressModels";
import Products from "../models/productModels";
import mongoose from "mongoose";
import Cart from "../models/cartModels";
import Category from "../models/categoryModels";
import Orders from "../models/orderModels";
import Coupon from "../models/couponModel";
const { generateRazorpay } = require("../config/razorpay");

const ObjectId = mongoose.Types.ObjectId;

dotenv.config();

const accountSid = process.env.TWILIO_ACCOUNT_SID;
const authToken = process.env.TWILIO_AUTH_TOKEN;
const client = require("twilio")(accountSid, authToken);

export default {
  homePage: async (req, res, next) => {
    let user = req.session.user;

    try {
      const allproducts = await Products.find();
      const allcategory = await Category.find();
      const products = await Products.find();
      const Coupons= await Coupon.find()

      if (req.session.user) {
        let cartCount = await userHelpers.getCartCount(req.session.user._id);

        console.log(cartCount);
        res.render("shop/home.ejs", {
          user,
          allproducts,
          cartCount,
          allcategory,
          products,
          Coupons
        });
      } else {
        let cartCount = null;
        res.render("shop/home.ejs", {
          user: false,
          allproducts,
          cartCount,
          allcategory,
          products,
          Coupons
        });
      }
    } catch (error) {
      console.error(error);
      res.render("catchError", {
        message: err.message,
        user: req.session.user,
      });
    }
  },

  loginPage: async (req, res) => {
    res.render("shop/userlogin/login");
  },

  GetOtpLogin: (req, res) => {
    res.render("shop/userlogin/otp-login.ejs");
  },
  GetOtpSend: (req, res) => {
    res.render("shop/userlogin/otp-send.ejs");
  },

  //signup

  signupWithOtp: (req, res) => {
    res.render("shop/userlogin/signupWithOtp.ejs");
  },


  signUpPage: (req, res) => {
    const phone = req.query.phone;
    res.render("shop/userlogin/signup.ejs", { phone: phone });
  },
  getForgotPassword: (req, res) => {
    res.render("shop/userlogin/forgotPassword.ejs");
  },
  generateOtp: (req, res) => {
    console.log(req.body);
    userHelpers.generateOtp(req.body.phonenumber).then((user) => {
      let response = user;
      if (response.status) {
        const msg1 = "OTP SENT!!";
        res.render("shop/userlogin/verify-otp", {
          msg1,
          phonenumber: req.body.phonenumber,
        });
      } else {
        res.render("shop/userlogin/signup");
      }
    });
  },
  generateOtpForSignup: async (req, res) => {
    try {
      const response = await userHelpers.generateOtpForSignup(req.body.phonenumber);
      res.json(response);
    } catch (error) {
      console.error(error);
      res.status(500).json({ status: false });
    }
  },
  verifyOtpForSignup: async (req, res) => {
    console.log(req.body);
    try {
      const phonenumber = req.body.phonenumber;
      const otp = req.body.otp;
      console.log(phonenumber);
      console.log(otp);
  
      const verificationChecks = await client.verify.v2.services("VA90f6a161ac014c889176b5d2e630bcde")
        .verificationChecks.create({ to: `+91${phonenumber}`, code: otp });
  
      if (verificationChecks.status === "approved") {
        // Redirect to the signUpPage
        res.json({ status: "success" });
      } else {
        res.json({ status: "error" }); // Send error response as JSON object
      }
    } catch (err) {
      console.error(err);
      return res.render("catchError", {
        message: err.message,
      });
    }
  },
  
  
  

  generateOtpForPassword: async (req, res) => {
    userHelpers
      .generateOtpForPassword(req.body.phonenumber)
      .then((response) => {
        res.json(response);
      })
      .catch((err) => {
        console.error(err);
        res.status(500).json({ status: false });
        console.error(err);
        res.render("catchError", {
          message: err.message,
        });
      });
  },

  verifyOtpForPassword: async (req, res) => {
    console.log(req.body);
    try {
      const phonenumber = req.body.phonenumber;
      const otp = req.body.otp;
      console.log(phonenumber);
      console.log(otp);

      client.verify.v2
        .services("VA90f6a161ac014c889176b5d2e630bcde")
        .verificationChecks.create({ to: `+91${phonenumber}`, code: otp })
        .then(async (verificationChecks) => {
          if (verificationChecks.status === "approved") {
            res.json({ status: "success" }); // Send success response as JSON object
          } else {
            res.json({ status: "error" }); // Send error response as JSON object
          }
        });
    } catch (err) {
      console.error(err);
      res.render("catchError", {
        message: err.message,
      });
    }
  },
  resetPassword: async (req, res) => {
